sequential.script:
var F = java.nio.file.Files;
var P = java.nio.file.Paths;
for each (var line in F.readAllLines(P.get('@{srcfile}'))){
    print(line);
}

var F = java.nio.file.Files;
var P = java.nio.file.Paths;
var s = F.walk(P.get('@{srcdir}'));
try {
    s.filter(function(p) F.isRegularFile(p))
     .forEach(function(p) {
        print(p);
        for each (var line in F.readAllLines(p)){
            print(line);
        }
     });
} finally {
    s.close();
}
