sequential.script:
var p = self.getProject();
var arch = p.getProperty('os.arch');
if (arch.endsWith('64')){
    p.setProperty('@{property}', '64');
} else {
    p.setProperty('@{property}', '32');
}

var p = self.getProject();
var os = p.getProperty('os.name');
if (os.contains('win')) {
    p.setProperty('@{windos}', '@{windows}');
} else if (os.contains('nux') || os.contains('nix')) {
    p.setProperty('@{windos}', '@{linux}');
} else if (os.contains('mac')) {
    p.setProperty('@{windos}', '@{macos}');
}
